import { Link } from 'react-router-dom'
import { PageWrapper, PageContent, Headline, PageImage } from 'Layouts'
import { Input } from '@tablecheck/tablekit-input'
import { SearchInput, SearchPageContent, Autocomplete } from './styles'
import { useRef, useState } from 'react'
import { useEffect } from 'react'
import { InputShape } from '@tablecheck/tablekit-input'
import { Item, ItemGroup } from '@tablecheck/tablekit-item'

// NOTEs
// the last call for handlechange doensn't fire when cunsective hits
// loader

type Listing = {
  locations?: [{ text: string; type: string; payload: { geo: {} } }]
  cuisines?: []
  shops?: [{ text: string; payload: { shop_slug: string } }]
}

export function Search(): JSX.Element {
  const [searchQuery, setSearchQuery] = useState('')
  const [listing, setListing] = useState<Listing>({})

  const handleChange = async () => {
    try {
      const res = await fetch(
        `https://staging-snap.tablecheck.com/v2/autocomplete?locale=en&shop_universe_id=57e0b91744aea12988000001&text=${searchQuery}`,
      )
      if (res.status !== 200) {
        alert('Error happened. Please try again later.')
        throw new Error('Error happened. Please try again later.')
      }
      const data = await res.json()
      console.log(data)
      setListing(data)
    } catch (e) {
      console.error(e)
    }
  }

  useEffect(() => {
    if (searchQuery) {
      handleChange()
    } else {
      setListing({})
    }
  }, [searchQuery])

  const autocomplete = (
    <ItemGroup
      headingText={listing.locations && <p>AREA</p>}
      headingStyles={{ color: 'blue' }}
    >
      {/* {listing.locations && <p>AREA</p>} */}

      {listing.locations?.map((location, idx) => (
        <Item>
          <Link
            to={`shops?term=${searchQuery}`}
            key={idx}
            style={{ display: 'block', textDecoration: 'none' }}
            state={location.payload.geo}
          >
            {location.text}
          </Link>
        </Item>
      ))}
    </ItemGroup>
    // {listing.locations && <p>VENUE</p>}
    // {listing.shops?.map((shop, idx) => (
    //   <Link
    //     to={shop.payload.shop_slug}
    //     key={idx}
    //     style={{ display: 'block' }}
    //     state={shop.payload.shop_slug}
    //   >
    //     {shop.text}
    //   </Link>
    // ))}
  )

  return (
    <SearchPageContent>
      <SearchInput
        type="search"
        placeholder="Area, cuisine or venue"
        name="term"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        shape={InputShape.Sharp}
        shouldFitContainer
        message={autocomplete}
        inputCss={{ margin: '100px' }}
      />
    </SearchPageContent>
  )
}
