import { Helmet } from 'react-helmet'
import { useTranslation } from 'react-i18next'
import { Outlet } from 'react-router-dom'
import { PageWrapper, PageContent, Headline, PageImage } from 'Layouts'
import { Button } from '@tablecheck/tablekit-button'
import { Input } from '@tablecheck/tablekit-input'

import { HomeHeadline, HomeWrapper } from './styles'
import { useEffect, useState } from 'react'
import { Link, useLocation } from 'react-router-dom'

export function ShopDetails(): JSX.Element {
  const [shopDetails, setShopDetails] = useState({
    name: [],
    search_image: string,
  })

  const location = useLocation()
  const shop: any = location.state
  console.log('shop', shop)

  const getShopDetails = async () => {
    try {
      const res = await fetch(
        `https://staging-snap.tablecheck.com/v2/shops/${shop}?sort=slug&sort_by=slug&sort_order=asc`,
      )
      const data = await res.json()
      setShopDetails(data.shops[0])
      console.log('data1', shopDetails)
      console.log('data2', data)
    } catch (error) {}
  }

  useEffect(() => {
    getShopDetails()
  }, [])

  return (
    // <PageContent>
    <>
      <div>
        <div>
          <h2>{shopDetails.name[1]}</h2>
          <p>
            {/* <span>{shopDetails.alt_address.stree2}</span>
            <span>{shopDetails.alt_address.street1}</span>
            <span>{shopDetails.alt_address.city}</span> */}
          </p>
        </div>
        <div>
          <img src={shopDetails.search_image} alt="shop" />
        </div>
      </div>
    </>
    // </PageContent>
  )
}
