import { Helmet } from 'react-helmet'
import { useTranslation } from 'react-i18next'
import { PageWrapper, PageContent, Headline, PageImage } from 'Layouts'
import { Button } from '@tablecheck/tablekit-button'
import { Input } from '@tablecheck/tablekit-input'

import { HomeHeadline, HomeWrapper } from './styles'
import { useEffect, useState } from 'react'
import { Link, useLocation } from 'react-router-dom'

export function Listing(): JSX.Element {
  const [shopList, setShopList] = useState([])
  const [geoCoordinates, setGeoCoordinates] = useState({ lat: '', lon: '' })

  const { state } = useLocation()
  // const geoCoordinates = location.state
  console.log('location', location.state)

  const queryParams = new URLSearchParams(window.location.search)
  const term = queryParams.get('term')
  console.log('term', term)

  const getListing = async () => {
    try {
      setGeoCoordinates({ lat: location.state.lat, lon: location.state.lon })
      console.log('geo', geoCoordinates)

      const res = await fetch(
        `https://staging-snap.tablecheck.com/v2/shop_search?geo_latitude=${geoCoordinates.lat}&geo_longitude=${geoCoordinates.lon}&shop_universe_id=57e0b91744aea12988000001&locale=en&per_page=50`,
      )
      const data = await res.json()
      console.log(data)
      setShopList(data.shops)
    } catch (e) {
      console.error(e)
    }
  }

  const updateListing = async () => {
    try {
      const res = await fetch(
        `https://staging-snap.tablecheck.com/v2/autocomplete?locale=en&shop_universe_id=57e0b91744aea12988000001&text=${term}`,
      )
      const data = await res.json()
      console.log('---', data.locations[0].payload.geo)

      const geoCoordinates = data.locations[0].payload.geo

      const res2 = await fetch(
        `https://staging-snap.tablecheck.com/v2/shop_search?geo_latitude=${geoCoordinates.lat}&geo_longitude=${geoCoordinates.lon}&shop_universe_id=57e0b91744aea12988000001&locale=en&per_page=50`,
      )
      const data2 = await res2.json()
      console.log(data2)
      setShopList(data2.shops)
    } catch (error) {
      console.error(error)
      alert('Error happened. Please try again later.')
    }
  }

  useEffect(() => {
    if (!term) {
      getListing()
    } else {
      updateListing()
    }
  }, [term])

  return (
    // <PageContent>
    <>
      {shopList.map((shop) => (
        <Link
          to={'shopdetails'}
          // to={`/:locale/${shop._id}`}
          key={shop._id}
          style={{ display: 'block' }}
          state={shop}
        >
          {shop.name}
        </Link>
      ))}
      {/* <Outlet /> */}
    </>
    // </PageContent>
  )
}
