import { Link } from 'react-router-dom'
import { PageWrapper, PageContent, Headline, PageImage } from 'Layouts'
import { Input } from '@tablecheck/tablekit-input'
import { SearchInput, SearchPageContent, SearchSpinner } from './styles'
import { useRef, useState } from 'react'
import { useEffect } from 'react'
import { InputShape } from '@tablecheck/tablekit-input'
import { Item, ItemGroup } from '@tablecheck/tablekit-item'
import { css } from '@emotion/react'
import { Spinner, SpinnerSize } from '@tablecheck/tablekit-spinner'

// NOTEs
// the last call for handlechange doensn't fire when cunsective hits
// loader

type Listing = {
  locations?: [{ text: string; type: string; payload: { geo: {} } }]
  cuisines?: []
  shops?: [{ text: string; payload: { shop_slug: string } }]
}

export function Search(): JSX.Element {
  const [searchQuery, setSearchQuery] = useState('')
  const [listing, setListing] = useState<Listing>({})
  const [isLoading, setIsLoading] = useState(false)

  const handleChange = async () => {
    try {
      setIsLoading((prev) => !prev) // spinner to true
      const res = await fetch(
        `https://staging-snap.tablecheck.com/v2/autocomplete?locale=en&shop_universe_id=57e0b91744aea12988000001&text=${searchQuery}`,
      )
      if (res.status !== 200) {
        alert('Error happened. Please try again later.')
        throw new Error('Error happened. Please try again later.')
      }
      const data = await res.json()
      console.log(data)
      setListing(data)
      setIsLoading((prev) => !prev)
      // sppiner to false
    } catch (e) {
      console.error(e)
    }
  }

  useEffect(() => {
    if (searchQuery) {
      handleChange()
    } else {
      setListing({})
    }
  }, [searchQuery])

  const autocomplete = (
    <>{isLoading ? <SearchSpinner size={SpinnerSize.Regular} /> : <></>}</>
  )

  return (
    <SearchPageContent>
      <SearchInput
        type="search"
        placeholder="Area, cuisine or venue"
        name="term"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        shape={InputShape.Sharp}
        shouldFitContainer
        isMessageHidden={searchQuery.length === 0}
        message={autocomplete}
      />
    </SearchPageContent>
  )
}
