import { Helmet } from 'react-helmet'
import { useTranslation } from 'react-i18next'
import { Outlet, useNavigate } from 'react-router-dom'
import { PageWrapper, PageContent, Headline, PageImage } from 'Layouts'
import { Button } from '@tablecheck/tablekit-button'
import { Input } from '@tablecheck/tablekit-input'

import { HomeHeadline, HomeWrapper } from './styles'
import { useState } from 'react'
import { Listing } from 'Pages/Listing'

export function Search(): JSX.Element {
  const [searchQuery, setSearchQuery] = useState('')
  const [listing, setListing] = useState({})
  const [locations, setLocations] = useState<
    { text: number; type: string; payload: {} }[]
  >([])
  const [shops, setShops] = useState([])

  const navigate = useNavigate()

  const handleSearch = async (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault()

    const query = e.target.value

    try {
      const res = await fetch(
        `https://staging-snap.tablecheck.com/v2/autocomplete?locale=en&shop_universe_id=57e0b91744aea12988000001&text=${query}`,
      )
      const data = await res.json()
      console.log(data)
      // setListing(data)
      setLocations(data.locations)
      setShops(data.shops)
      // navigate('shops')
    } catch (error) {
      console.error(error)
      alert('Error happened. Please try again later.')
    }
  }

  return (
    // <PageContent>
    <div>
      {/* <form onSubmit={(e) => handleSearch(e)} action="/shops"> */}
      <Input
        type="search"
        placeholder="Area"
        value={searchQuery}
        // onChange={(e) => setSearchQuery(e.target.value)}
        onChange={(e) => handleSearch(e)}
      />
      {/* <Button type="submit">Search</Button> */}
      {/* </form> */}
      <Listing locations={locations} shops={shops} />
    </div>
    // </PageContent>
  )
}
