import { Link } from 'react-router-dom'
import { PageWrapper, PageContent, Headline, PageImage } from 'Layouts'
import { Input } from '@tablecheck/tablekit-input'
import { SearchInput, SearchPageContent, SearchSpinner } from './styles'
import { useRef, useState } from 'react'
import { useEffect } from 'react'
import { InputShape } from '@tablecheck/tablekit-input'
import { Item, ItemGroup } from '@tablecheck/tablekit-item'
import { css } from '@emotion/react'
import { Spinner, SpinnerSize } from '@tablecheck/tablekit-spinner'
import { Icon } from '@tablecheck/tablekit-icon'
import '@tablecheck/tablekit-free-icon-config'
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { tci0Square } from 'tablecheck-icons/tci0Square'
import { faEdit } from '@fortawesome/free-regular-svg-icons/faEdit'
// import { faLight } from '@fortawesome/free-regular-svg-icons/fa
import { faLocationArrow, faMapPin } from '@fortawesome/free-solid-svg-icons'

// NOTEs
// the last call for handlechange doensn't fire when cunsective hits
// loader

type Listing = {
  locations?: [{ text: string; type: string; payload: { geo: {} } }]
  cuisines?: []
  shops?: [{ text: string; payload: { shop_slug: string } }]
}

export function Search(): JSX.Element {
  const [searchQuery, setSearchQuery] = useState('')
  const [listing, setListing] = useState<Listing>({})
  const [isLoading, setIsLoading] = useState(false)

  const handleChange = async () => {
    try {
      setIsLoading((prev) => !prev) // spinner to true
      const res = await fetch(
        `https://staging-snap.tablecheck.com/v2/autocomplete?locale=en&shop_universe_id=57e0b91744aea12988000001&text=${searchQuery}`,
      )
      if (res.status !== 200) {
        alert('Error happened. Please try again later.')
        throw new Error('Error happened. Please try again later.')
      }
      const data = await res.json()
      console.log(data)
      setListing(data)
      setIsLoading((prev) => !prev)
      // sppiner to false
    } catch (e) {
      console.error(e)
    }
  }

  useEffect(() => {
    if (searchQuery) {
      handleChange()
    } else {
      setListing({})
    }
  }, [searchQuery])

  const autocomplete = (
    <>
      {isLoading ? (
        <SearchSpinner size={SpinnerSize.Regular} />
      ) : (
        <>
          <ItemGroup
            headingText={
              listing.locations && (
                <span>
                  <Icon icon={faEdit} />
                  <Icon icon={faLocationArrow} />
                  <Icon icon={faMapPin} />
                  {/* <Icon icon={{ iconName: 'tciLIne' }} /> */}
                  {/* <Icon icon={{ icon: 'fa-light fa-location-dot' }} /> */}
                  {/* <FontAwesomeIcon icon="fa-light fa-location-dot" /> */}
                  {/* <Icon
                    icon={{
                      icon: [
                        576,
                        512,
                        [],
                        'f044',
                        'M402.6 83.2l90.2 90.2c3.8 3.8 3.8 10 0 13.8L274.4 405.6l-92.8 10.3c-12.4 1.4-22.9-9.1-21.5-21.5l10.3-92.8L388.8 83.2c3.8-3.8 10-3.8 13.8 0zm162-22.9l-48.8-48.8c-15.2-15.2-39.9-15.2-55.2 0l-35.4 35.4c-3.8 3.8-3.8 10 0 13.8l90.2 90.2c3.8 3.8 10 3.8 13.8 0l35.4-35.4c15.2-15.3 15.2-40 0-55.2zM384 346.2V448H64V128h229.8c3.2 0 6.2-1.3 8.5-3.5l40-40c7.6-7.6 2.2-20.5-8.5-20.5H48C21.5 64 0 85.5 0 112v352c0 26.5 21.5 48 48 48h352c26.5 0 48-21.5 48-48V306.2c0-10.7-12.9-16-20.5-8.5l-40 40c-2.2 2.3-3.5 5.3-3.5 8.5z',
                      ],
                      iconName: 'edit',
                      prefix: 'fas',
                    }}
                  /> */}
                  AREA
                </span>
              )
            }
            headingStyles={{ color: 'blue' }}
          >
            {listing.locations?.map((location, idx) => (
              <Item>
                <Link
                  to={`shops?term=${searchQuery}`}
                  key={idx}
                  style={{ display: 'block', textDecoration: 'none' }}
                  state={location.payload.geo}
                >
                  {location.text}
                </Link>
              </Item>
            ))}
          </ItemGroup>
          <ItemGroup headingText={listing.locations && 'VENUE'}>
            {listing.shops?.map((shop, idx) => (
              <Item key={idx}>
                <Link
                  to={shop.payload.shop_slug}
                  style={{ display: 'block' }}
                  state={shop.payload.shop_slug}
                >
                  {shop.text}
                </Link>
              </Item>
            ))}
          </ItemGroup>
        </>
      )}
    </>
  )

  return (
    <SearchPageContent>
      <SearchInput
        type="search"
        placeholder="Area, cuisine or venue"
        name="term"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        shape={InputShape.Sharp}
        shouldFitContainer
        isMessageHidden={searchQuery.length === 0}
        message={autocomplete}
      />
    </SearchPageContent>
  )
}
